mapper.select:
select 

from HS_APPLY
where APPLY_ID = #{applyId,jdbcType=VARCHAR}

SELECT AP.APPLY_ID, AP.CREDIT_SESAME, AP.PARTICLES_BORROW, AP.APPLY_DATE, AP.AUDIT_TYPE, AP.SOURCES, AP.PROC_STATUS_VAL, AP.PROC_STATUS, AP.PROC_INST_ID, AP.SUGGEST_RATE, AP.SUGGEST_AMOUNT, AP.PRODUCT_PAY_VAL, AP.PRODUCT_PAY, AP.PRODUCT_FEE_RATE, AP.PRODUCT_RATE, AP.APPLY_PERIOD, AP.APPLY_AMOUNT, AP.PRODUCT_NAME, AP.PRODUCT_ID, AP.ID_ADDR, AP.CUST_MOBILE, AP.CUST_AGE, AP.CUST_SEX, AP.ID_TYPE_VAL, AP.ID_TYPE, AP.ID_NUMBER, AP.CUST_NAME, AP.LOAN_ID FROM ( SELECT AP.*, ROWNUM r FROM HS_APPLY AP WHERE ROWNUM <= (#{pageIndex}/#{pageSize}+1)*#{pageSize} ) AP WHERE 1 <> 2
and CUST_NAME like #{custName}
and LOAN_ID = #{loanId}
AND r > 0

  	select count(APPLY_ID) from HS_APPLY where 1<>2

			and CUST_NAME like #{custName}  


			and LOAN_ID = #{loanId}

mapper.update:
update HS_APPLY set
PROC_INST_ID=#{processId},
PROC_STATUS_VAL=#{precessStatusVal},PROC_STATUS=#{precessStatus}
where APPLY_ID=#{applyId}

update HS_APPLY set
SIGNCONFIRM_VAL = #{signconfirmVal,jdbcType=VARCHAR},SIGNCONFIRM = #{signconfirm,jdbcType=INTEGER}
where APPLY_ID=#{applyId}

mapper.select:
SELECT * FROM ( SELECT hsa.APPLY_ID, hsa.PROC_STATUS_VAL, hsa.LOAN_ID, hsa.SALES_EMP_NAME, hsa.CREATE_EMPLOYEE_VAL, hsa.PRODUCT_FEE_RATE, hsa.APPLY_PERIOD, hsa.CUST_NAME, REPLACE ( hsa.ID_NUMBER, SUBSTR (hsa.ID_NUMBER, 7, 8), '********' ) ID_NUMBER, REPLACE ( hsa.CUST_MOBILE, SUBSTR (hsa.CUST_MOBILE, 4, 4), '****' ) CUST_MOBILE, hsa.PRODUCT_NAME, hsa.APPLY_DATE, hsa.APPLY_AMOUNT, hsa.SUGGEST_AMOUNT, hsa.CUST_SEX, hsa.SOURCES_VALUE, ROWNUM AS r FROM ( SELECT ap.APPLY_ID, ap.PROC_STATUS_VAL, ap.PROC_INST_ID, ap.LOAN_ID, ap.SALES_EMP_NAME, ap.CREATE_EMPLOYEE_VAL, ap.PRODUCT_FEE_RATE, ap.APPLY_PERIOD, ap.CUST_NAME, ap.ID_NUMBER, ap.CUST_MOBILE, ap.PRODUCT_NAME, ap.APPLY_DATE, ap.APPLY_AMOUNT, ap.SUGGEST_AMOUNT, ap.CUST_SEX, ap.SOURCES_VALUE, ap.DEL_STATUE, ap.CREATE_EMPLOYEE, ap.STORE_ID FROM HS_APPLY ap WHERE ap.DEL_STATUE = 0 AND ap.STORE_ID=#{storeId}
AND (ap.CREATE_EMPLOYEE IS NULL OR ap.CREATE_EMPLOYEE=#{empId})
AND ap.ID_NUMBER=#{idNumber}
AND ap.LOAN_ID=#{loanId}
AND ap.CUST_NAME LIKE #{custName}
AND ap.CUST_MOBILE=#{custMobile}
AND ap.PRODUCT_ID LIKE #{product}
AND ap.SOURCES = #{source}
AND ap.SALES_EMP_NAME LIKE #{salesEmpName}
AND ap.PROC_INST_ID IS NULL AND PROC_STATUS IS NULL
AND (ap.PROC_INST_ID IS NOT NULL OR PROC_STATUS IS NOT NULL)
ORDER BY ap.APPLY_DATE DESC ) hsa WHERE ROWNUM <= #{pageIndex}*#{pageSize} ) WHERE r > (#{pageIndex}-1)*#{pageSize}


select count(hsa.APPLY_ID) from HS_APPLY hsa 
WHERE HSA.CREATE_EMPLOYEE IS NOT NULL AND HSA.DEL_STATUE=0 AND hsa.STORE_ID=#{storeId}


	AND hsa.CREATE_EMPLOYEE=#{empId}


AND hsa.ID_NUMBER=#{idNumber}


AND hsa.LOAN_ID=#{loanId}


AND hsa.CUST_NAME LIKE #{custName}


AND hsa.CUST_MOBILE=#{custMobile}


AND hsa.SALES_EMP_NAME LIKE #{salesEmpName}




	AND hsa.PROC_INST_ID IS NULL



	AND hsa.PROC_INST_ID IS NOT NULL

SELECT AP.* FROM ( SELECT ROWNUM r, app.LOAN_ID, app.APPLY_ID, app.CUST_NAME, app.SOURCES, app.SOURCES_VALUE, REPLACE (app.CUST_MOBILE, SUBSTR (app.CUST_MOBILE, 4, 4), '****') AS CUST_MOBILE, REPLACE (app.ID_NUMBER, SUBSTR (app.ID_NUMBER, 6, 8),'********') AS ID_NUMBER, app.PRODUCT_ID,app.PRODUCT_NAME,app.APPLY_AMOUNT,app.PROC_STATUS_VAL, app.PROC_STATUS,app.PROC_INST_ID,app.STORE_ID,app.SALES_EMP_ID, app.SALES_EMP_NAME,app.CREATE_EMPLOYEE,app.CREATE_EMPLOYEE_VAL,app.APPLY_DATE, tbl.ASSIGNEE_ FROM HS_APPLY app RIGHT JOIN ( SELECT HP.BUSINESS_KEY_, HT.ASSIGNEE_ FROM ACT_HI_PROCINST HP INNER JOIN ACT_HI_TASKINST HT ON HP.PROC_INST_ID_ = HT.PROC_INST_ID_
WHERE HT.TASK_DEF_KEY_ = 'addData' AND HT.ASSIGNEE_ IS NULL AND HT.END_TIME_ IS NULL
LEFT JOIN HS_EMPLOYEE EMP ON HT.ASSIGNEE_ = EMP.EMP_ID RIGHT JOIN ( SELECT CIL.DEPT_ID FROM HS_DEPARTMENT CIL START WITH CIL.DEPT_ID=#{deptId} CONNECT BY PRIOR CIL.DEPT_ID=CIL.DEPT_PARENT ) TDEPT ON TDEPT.DEPT_ID=EMP.DEPT_ID WHERE HT.TASK_DEF_KEY_ = 'addData' and HT.ASSIGNEE_ is not null AND HT.END_TIME_ IS NULL
WHERE HT.TASK_DEF_KEY_ = 'addData' AND (HT.ASSIGNEE_=#{empId} and HT.ASSIGNEE_ IS not NULL AND HT.END_TIME_ IS NULL)
LEFT JOIN HS_EMPLOYEE EMP ON HT.ASSIGNEE_ = EMP.EMP_ID RIGHT JOIN ( SELECT CIL.DEPT_ID FROM HS_DEPARTMENT CIL START WITH CIL.DEPT_ID=#{deptId} CONNECT BY PRIOR CIL.DEPT_ID=CIL.DEPT_PARENT ) TDEPT ON TDEPT.DEPT_ID=EMP.DEPT_ID WHERE HT.TASK_DEF_KEY_ = 'addData' AND HT.END_TIME_ IS NOT NULL
WHERE HT.TASK_DEF_KEY_ = 'addData' AND HT.END_TIME_ IS NOT NULL AND HT.ASSIGNEE_=#{empId}
LEFT JOIN HS_EMPLOYEE EMP ON HT.ASSIGNEE_ = EMP.EMP_ID RIGHT JOIN ( SELECT CIL.DEPT_ID FROM HS_DEPARTMENT CIL START WITH CIL.DEPT_ID=#{deptId} CONNECT BY PRIOR CIL.DEPT_ID=CIL.DEPT_PARENT ) TDEPT ON TDEPT.DEPT_ID=EMP.DEPT_ID WHERE HT.TASK_DEF_KEY_ = 'addData'
WHERE HT.TASK_DEF_KEY_ = 'addData' AND HT.ASSIGNEE_=#{empId}
GROUP BY HT.ASSIGNEE_, HP.BUSINESS_KEY_ ) tbl ON app.APPLY_ID=tbl.BUSINESS_KEY_ WHERE app.DEL_STATUE = 0 AND app.STORE_ID = #{storeId} AND ROWNUM <= #{pageIndex} * #{pageSize}
and app.CUST_NAME LIKE #{custName}
and app.CUST_MOBILE = #{custMobile}
and app.ID_NUMBER = #{idNumber}
and app.SALES_EMP_NAME LIKE #{salesEmpName}
and app.LOAN_ID = #{loanId}
ORDER BY app.APPLY_DATE DESC ) AP WHERE r > (#{pageIndex} -1) * #{pageSize}

SELECT COUNT(app.APPLY_ID) FROM HS_APPLY app RIGHT JOIN ( SELECT HP.BUSINESS_KEY_, HT.ASSIGNEE_ FROM ACT_HI_PROCINST HP INNER JOIN ACT_HI_TASKINST HT ON HP.PROC_INST_ID_ = HT.PROC_INST_ID_
WHERE HT.TASK_DEF_KEY_ = 'addData' AND HT.END_TIME_ IS NULL AND HT.ASSIGNEE_ IS NULL
LEFT JOIN HS_EMPLOYEE EMP ON HT.ASSIGNEE_ = EMP.EMP_ID RIGHT JOIN ( SELECT CIL.DEPT_ID FROM HS_DEPARTMENT CIL START WITH CIL.DEPT_ID=#{deptId} CONNECT BY PRIOR CIL.DEPT_ID=CIL.DEPT_PARENT ) TDEPT ON TDEPT.DEPT_ID=EMP.DEPT_ID WHERE HT.TASK_DEF_KEY_ = 'addData' AND HT.END_TIME_ IS NULL
WHERE HT.TASK_DEF_KEY_ = 'addData' AND (HT.ASSIGNEE_=#{empId} OR HT.ASSIGNEE_ IS NULL)
LEFT JOIN HS_EMPLOYEE EMP ON HT.ASSIGNEE_ = EMP.EMP_ID RIGHT JOIN ( SELECT CIL.DEPT_ID FROM HS_DEPARTMENT CIL START WITH CIL.DEPT_ID=#{deptId} CONNECT BY PRIOR CIL.DEPT_ID=CIL.DEPT_PARENT ) TDEPT ON TDEPT.DEPT_ID=EMP.DEPT_ID WHERE HT.TASK_DEF_KEY_ = 'addData' AND HT.END_TIME_ IS NOT NULL
WHERE HT.TASK_DEF_KEY_ = 'addData' AND HT.END_TIME_ IS NOT NULL AND HT.ASSIGNEE_=#{empId}
LEFT JOIN HS_EMPLOYEE EMP ON HT.ASSIGNEE_ = EMP.EMP_ID RIGHT JOIN ( SELECT CIL.DEPT_ID FROM HS_DEPARTMENT CIL START WITH CIL.DEPT_ID=#{deptId} CONNECT BY PRIOR CIL.DEPT_ID=CIL.DEPT_PARENT ) TDEPT ON TDEPT.DEPT_ID=EMP.DEPT_ID WHERE HT.TASK_DEF_KEY_ = 'addData'
WHERE HT.TASK_DEF_KEY_ = 'addData' AND HT.ASSIGNEE_=#{empId}
GROUP BY HT.ASSIGNEE_, HP.BUSINESS_KEY_ ) tbl ON app.APPLY_ID=tbl.BUSINESS_KEY_ WHERE app.DEL_STATUE = 0 AND app.STORE_ID = #{storeId}
and app.CUST_NAME LIKE #{custName}
and app.CUST_MOBILE = #{custMobile}
and app.ID_NUMBER = #{idNumber}
and app.SALES_EMP_NAME LIKE #{salesEmpName}
and app.LOAN_ID = #{loanId}

SELECT * FROM 
HS_APPLY a LEFT JOIN HS_CONTRACT  c ON a.APPLY_ID=c.APPLY_ID
WHERE a.APPLY_ID=#{applyId}


    SELECT TASK_DEF_KEY_,NAME_,START_TIME_,END_TIME_ FROM 	act_hi_taskinst 
	WHERE  PROC_INST_ID_=#{precessId}
	ORDER BY START_TIME_  DESC

		SELECT * FROM (
		    SELECT * FROM (
		        SELECT 
		            ROWNUM rr,
					hsapp.*
				FROM (
					SELECT 
					hsa.APPLY_ID,
					hsa.LOAN_ID,
					hsa.CUST_NAME,
					hsa.APPLY_AMOUNT,
					hsa.PRODUCT_NAME,
					REPLACE (hsa.CUST_MOBILE, SUBSTR (hsa.CUST_MOBILE, 4, 4), '****') AS CUST_MOBILE,
					REPLACE (hsa.ID_NUMBER, SUBSTR (hsa.ID_NUMBER, 6, 8),'********') AS ID_NUMBER,
					hsa.PROC_STATUS_VAL,
					hsa.SOURCES_VALUE,
					hsa.STORE_ID,
					hsa.STORE_NAME,
					hsa.SALES_EMP_DEPT_ID,
					hsa.SALES_EMP_DEPT_NAME,
					hsa.SALES_EMP_ID,
					hsa.SALES_EMP_NAME,
					hsa.CREATE_EMPLOYEE_VAL,
					hsa.APPLY_PERIOD,
					hsa.APPLY_DATE
					FROM HS_APPLY hsa
					WHERE HSA.CREATE_EMPLOYEE IS NOT NULL AND HSA.DEL_STATUE=0

						AND hsa.SALES_EMP_NAME LIKE #{salesEmpName}   


						AND hsa.ID_NUMBER=#{idNumber}


						AND hsa.CUST_NAME LIKE #{custName}


						AND hsa.CUST_MOBILE=#{custMobile}


						AND hsa.STORE_ID=#{orgId}


						AND hsa.LOAN_ID=#{loanId}




							AND hsa.SALES_EMP_ID=#{empId}



							AND hsa.SALES_EMP_DEPT_ID=#{deptId}



							AND hsa.STORE_ID=#{storeId}



							LEFT JOIN (
							SELECT CIL.DEPT_ID
							FROM HS_DEPARTMENT CIL
							WHERE CIL.STORE_FLAG=3 
							START WITH CIL.DEPT_ID=#{deptId}
							CONNECT BY PRIOR CIL.DEPT_ID=CIL.DEPT_PARENT
							) TBL ON hsa.STORE_ID=TBL.DEPT_ID


		             ORDER BY hsa.APPLY_DATE desc
		        ) hsapp
		    ) 
		    WHERE rr <= #{pageIndex} * #{pageSize}
		)  
 		WHERE ROWNUM > (#{pageIndex}-1)*#{pageSize}

SELECT 
	count(hsa.APPLY_ID)
